import { useContext, useReducer } from "react";
import styled from "styled-components";
import { NumContext } from "../../context/todos";
import TodoBody from "./TodoBody";
import TodoHeader from "./TodoHeader";
import TodoInput from "./TodoInput";
import { StrContext } from "../../context/todos";

function reducer(state, action) {
  switch (action.type) {
    case "CREATE_TODO":
      return state.concat({ id: action.id, text: action.text, done: false });
    case "TOGGLE_TODO":
      return state.map((todo) =>
        todo.id === action.id ? { ...todo, done: !todo.done } : todo
      );
    case "REMOVE_TODO":
      return state.filter((todo) => todo.id !== action.id);
    default:
      return state;
  }
}

function Todos() {
  const [todos, dispatch] = useReducer(reducer, [
    { id: 1, text: "투두리스트 스타일링", done: true },
    { id: 2, text: "투두리스트 기능 구현하기", done: false },
    { id: 3, text: "리팩토링 하기", done: false },
  ]);

  const num = useContext(NumContext);

  console.log("num context :", num);

  const Str = useContext(StrContext);

  console.log("Str context :", Str);

  return (
    <StrContext.Provider value={"Hello React"}>
      <NumContext.Provider value={1}>
        <Container>
          <TodoHeader todos={todos} />
          <TodoBody todos={todos} dispatch={dispatch} />
          <TodoInput dispatch={dispatch} />
        </Container>
      </NumContext.Provider>
    </StrContext.Provider>
  );
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  width: 350px;
  height: 700px;
  box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.4);

  background-color: #ffffff;
`;

export default Todos;
