import { createContext, useReducer, useState } from "react";

//creatContext(기본값) : 컨텍스트를 만든다.
//     => Provider 밖에서 useContext를 사용하면 반환되는 값.
export const NumContext = createContext(0);
export const StrContext = createContext("Hello React");

export function TodoProvider({ children }) {
  const [state, setState] = useState(1);
  return (
    <NumContext.Provider value={state}>
      <StrContext.Provider value={setState}>{children}</StrContext.Provider>
    </NumContext.Provider>
  );
}
