import styled from "styled-components";

function button({ text, bgColor }) {
  return <Container bgColor={bgColor}>{text}</Container>;
}

const Container = styled.div`
  width: 100px;
  padding: 5px 0;
  background-color: ${({ bgColor }) => bgColor};

  color: #fff;
  text-align: center;

  border-radius: 4px;
  & + & {
    margin-top: 5px;
  }
`;

export default button;
